import React, { useState, useRef } from 'react';
import './imageGenerator.css';
import default_image from '../Assets/default_image.svg';

const ImageGenerator = () => {
    const [imageUrl, setImageUrl] = useState("/");
    const inputRef = useRef(null);

    const imageGenerator = async () => {
        const prompt = inputRef.current.value.trim();

        if (prompt === "") {
            console.log("Input is empty.");
            return;
        }

        try {
            // Calling the Django backend (running on localhost:8000) instead of OpenAI API directly
            const response = await fetch("http://localhost:8000/api/generate-image/", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    prompt: prompt,  // Pass the prompt to the backend
                }),
            });

            if (!response.ok) {
                throw new Error("Failed to fetch image");
            }

            const data = await response.json();
            setImageUrl(data.image_url);

        } catch (error) {
            console.error("Error:", error);
        }
    };

    return (
        <div className='ai-image-generator'>
            <div className='header'>AI image <span>Generator</span></div>
            <div className='imgLoading'>
                <div className='image-loading'>
                    <div className='image'>
                        <img src={imageUrl === "/" ? default_image : imageUrl} alt="Generated representation" />
                    </div>
                </div>
            </div>
            <div className="search-box">
                <input
                    type="text"
                    className='search-input'
                    placeholder='Describe what you want to see?'
                    ref={inputRef}
                />
                <button className="generate-btn" onClick={imageGenerator}>Generate</button>
            </div>
        </div>
    );
}

export default ImageGenerator;
