import React, { useState, useRef } from 'react';
import './imageGenerator.css';
import default_image from '../Assets/default_image.svg';

const ImageGenerator = () => {
    const [imageUrl, setImageUrl] = useState("/");
    const inputRef = useRef(null);

    const imageGenerator = async () => {
        const prompt = inputRef.current.value.trim();

        if (prompt === "") {
            console.log("Input is empty.");
            return; // Exit if input is empty
        }

        try {
            console.log("Sending request to OpenAI API...");
            const response = await fetch("https://api.openai.com/v1/images/generations", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer sk-vDWU1XhTHPXvD466appyh6gXHorDkijDYFb04gHJ6YT3BlbkFJa8AI01ioLlqlLYj0Dl86rcS5a-MNZ1kAR1ZnohkwkA`, // Replace with your actual OpenAI API key
                },
                body: JSON.stringify({
                    prompt: prompt,
                    n: 1,
                    size: '512x512',
                }),
            });

            if (!response.ok) {
                throw new Error(`Network response was not ok: ${response.statusText}`);
            }

            const data = await response.json();
            const dataArray = data.data;

            if (dataArray && dataArray.length > 0) {
                setImageUrl(dataArray[0].url);
                console.log("Image URL set:", dataArray[0].url);
            } else {
                console.error("No images returned from API");
            }

        } catch (error) {
            console.error("Error fetching image:", error);
        }
    };

    return (
        <div className='ai-image-generator'>
            <div className='header'>AI image <span>Generator</span></div>
            <div className='imgLoading'>
                <div className='image-loading'>
                    <div className='image'>
                        <img src={imageUrl === "/" ? default_image : imageUrl} alt="Generated representation" />
                    </div>
                </div>
            </div>
            <div className="search-box">
                <input
                    type="text"
                    className='search-input'
                    placeholder='Describe what you want to see?'
                    ref={inputRef}
                />
                <button className="generate-btn" onClick={imageGenerator}>Generate</button> 
            </div>
        </div>
    );
}

export default ImageGenerator;
